import React from 'react';

export default class ProductItem extends React.Component {
  constructor(props) {
    super(props);
    this.state = {quantity: 1}
  }

  handleInputChange = event => 
      this.setState({[event.target.name]: event.target.value})

  addToCart = () => {
    localStorage.removeItem('cart');
    let cart = localStorage.getItem('cart') 
                  ? JSON.parse(localStorage.getItem('cart')) : {pid:[],quantity:[]};
    let id = this.props.product.pid.toString();

    cart[id] = (cart[id] ? cart[id]: 0);
    let qty = cart[id] + parseInt(this.state.quantity);
    if (this.props.product.quantity < qty) {
      cart[id] = this.props.product.quantity; 
    } else {
      cart[id] = qty
    }
    const cartJSON = {"pid":id,"quantity":cart[id]};

    localStorage.setItem('cart', JSON.stringify(cart));
    // console.log(JSON.stringify(cart[id]));
    // console.log(JSON.stringify(cart));
  }

  render(){
    const { product } = this.props;
    return (
     <div className="card" style={{ marginBottom: "10px"}}>
       <div className="card-body">
         <h4 className="card-title">{product.name}</h4>
         <p className="card-text">{product.description}</p>
         <h5 className="card-text"><small>price: </small>${product.price}</h5>
         <span className="card-text">
           <small>Available Quantity: </small>{product.quantity}
         </span>
         { product.quantity > 0 ?
          <div>
             <button className="btn btn-sm btn-warning float-right" 
                onClick={this.addToCart}>Add to cart</button>
             <input type="number" value={this.state.quantity} name="quantity" 
                onChange={this.handleInputChange} className="float-right" 
                style={{ width: "60px", marginRight: "10px", borderRadius: "3px"}}/>
          </div> : 
          <p className="text-danger"> product is out of stock </p>
        }
      </div>
    </div>
   )
 }
}
