import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
// import AdminHeader from "./layouts/AdminHeader";
// import AdminSideBar from "./layouts/AdminSideBar";
// import AdminFooter from "./layouts/AdminFooter";


export default function AddMedicine() {
  const navigate = useNavigate();
  const [categories, setCategories] = useState([]);
  //const categoriesCollectionReference = collection(db, "medicine_categories");
  const getCategories = async () => {
    const result = await axios.get("http://localhost:8081/categories");
    setCategories(result.data);
    console.log(result);
    console.log(categories);
  };

  useEffect(() => {
    getCategories();

  }, []);
  //const medicinesCollectionRef = collection(db, "medicine_inventory");
  const [errorMsg, setErrorMsg] = useState("");
  const [successMsg, setSuccessMsg] = useState("");
  const [product, setMedicine] = useState({
    name: "",
    brand: "",
    categoryid: "",
    description: "",
    price: "",
    productImage: "",
  });
  const handleAddMedicine = async () => {
    if (
      product.name &&
      product.power &&
      product.categoryid &&
      product.description &&
      product.price &&
      product.productImage
    ) {
      setErrorMsg("");
      await addDoc(productsCollectionRef, {
        name: product.name,
        power: product.power,
        categoryid: product.categoryid,
        description: product.description,
        price: product.price,
        productImage: product.productImage,
      });
      setSuccessMsg("Product added successfully!");
      setTimeout(() => {
        setSuccessMsg("");
        navigate("/inventory");
      }, 1000);
    } else {
      setErrorMsg("Please fill out all the required fields!");
    }
  };
  return (
    <>
      {/* <AdminHeader />
      <AdminSideBar /> */}
      <div className="main-panel">
        <div className="content">
          <div className="container-fluid">
            <h4 className="page-title">Create Product</h4>
            <div className="row">
              <div className="col-md-12">
                <div className="card">
                  <div className="card-header">
                    <div className="card-title">
                      New Product Details
                      <Link to="/inventory" className="btn btn-danger btn-sm float-right">
                        Go BACK
                      </Link>{" "}
                    </div>
                  </div>
                  <div className="card-body px-4">
                    <div className="form-group">
                      <label htmlFor="name">Product Name</label>
                      <input
                        type="text"
                        className="form-control"
                        value={product.name}
                        id="name"
                        onChange={(event) =>
                          setProduct((prev) => ({ ...prev, name: event.target.value }))
                        }
                        placeholder="Enter Product Name"
                      />
                    </div>

                    <div className="form-group">
                      <label htmlFor="pobrandwer">Medicine Brand</label>
                      <input
                        type="text"
                        className="form-control"
                        value={medicine.brand}
                        id="brand"
                        onChange={(event) =>
                          setMedicine((prev) => ({ ...prev, brand: event.target.value }))
                        }
                        placeholder="Enter Medicine Brand"
                      />
                    </div>
                    <div class="form-group">
                      <label for="exampleFormControlSelect1">Medicine Category</label>
                      <select
                        class="form-control"
                        onChange={(event) =>
                          setMedicine((prev) => ({ ...prev, category: event.target.value }))
                        }
                        id="exampleFormControlSelect1">
                        <option value="">Select a Category...</option>
                        {categories.map((category) => {
                          return <option value={category.name}>{category.name}</option>;
                        })}
                      </select>
                    </div>
                    <div class="form-group">
                      <label for="exampleFormControlSelect2">Medicine Type</label>
                      <select
                        class="form-control"
                        onChange={(event) =>
                          setMedicine((prev) => ({ ...prev, type: event.target.value }))
                        }
                        id="exampleFormControlSelect2">
                        <option value="">Select a Type...</option>
                        {medTypes.map((medType) => {
                          return <option value={medType.name}>{medType.name}</option>;
                        })}
                      </select>
                    </div>
                    <div className="form-group">
                      <label htmlFor="price">Medicine Price (in â‚¹.)</label>
                      <input
                        type="text"
                        className="form-control"
                        value={medicine.price}
                        id="price"
                        onChange={(event) =>
                          setMedicine((prev) => ({ ...prev, price: event.target.value }))
                        }
                        placeholder="Enter Medicine Price"
                      />
                    </div>
                    <div className="form-group">
                      <label htmlFor="stock">Medicine Stock</label>
                      <input
                        type="text"
                        className="form-control"
                        value={medicine.stock}
                        id="stock"
                        onChange={(event) =>
                          setMedicine((prev) => ({ ...prev, stock: event.target.value }))
                        }
                        placeholder="Enter Medicine Stock"
                      />
                    </div>
                  </div>

                  <div className="form-group px-4 mb-3">
                    <div className="text-center text-danger">{errorMsg}</div>
                    <div className="text-center text-success">{successMsg}</div>
                    <button className="btn btn-primary mx-3" onClick={handleAddMedicine}>
                      Add Medicine
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* <AdminFooter /> */}
      </div>
    </>
  );
}
